package com.capg.corejava.basics;

public class IncrementDecrementOpts {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10;
		int j = 20;
	    i = ++i;
		System.out.println(i);
		// j = j++;
		//System.out.println(j);
		int k = j++;
		System.out.println(k);
		System.out.println(j);
	}

}
